from tkinter import *import Validadordef ValidarExpresion(expresion, frase):    primero = Validador.FuncionValidar()    comando = Validador.Validador(primero, expresion, frase)    br = Validador.Broker()    br.respuesta(comando)raiz = Tk()raiz.title("Expresiones regulares")raiz.resizable(False,False)miFrame = Frame()miFrame.config(width="650",height="350")miFrame.pack()Expresion = Label(miFrame, text="Expresion: ")Expresion.grid(row=1,column=1,pady=5, padx=5)EntryExpresion = Entry(miFrame)EntryExpresion.grid(row=1, column=2,pady=5, padx=5)Frase = Label(miFrame,text="Frase: ")Frase.grid(row=2, column=1,pady=5, padx=5)EntryFrase = Entry(miFrame)EntryFrase.grid(row=2, column=2,pady=5, padx=5)Validar = Button(miFrame, text="Validar", command=lambda:ValidarExpresion(EntryExpresion.get(),EntryFrase.get()))Validar.grid(row=3,column=2,pady=5, padx=5)raiz.mainloop()